config.formatElementScript:
new UIPopover(el.appendChild(new Element('span', {"class":"user-info"})),{
        content:'<pre>item json</pre>',
        anchor:UIPopover.AnchorAuto()
    });



var read=item.getAccess().editAccessEnd;
var write=item.getAccess().readAccessEnd;
var addDecoration=function(time, className){

  if(parseInt(time)>parseInt((new Date()).getTime()/1000)){
      el.addClass(className);
  }

};
addDecoration(read, 'has-read');
addDecoration(write, 'has-write');

if(item.getId()+""==AppClient.getId()+""){
    el.addClass('is-user');
}

if(item.getRoles().indexOf("special")>=0){
    el.addClass('is-admin');
}
